    void loadDD(){
        ArrayList<TblDiadiem>list;
        try{
        list = dddl.getListDD();
        tablemodel = (DefaultTableModel) tblDD.getModel();
        for(TblDiadiem dd : list){
            tablemodel.addRow(new Object[]{dd.getMadd(), dd.getTendd(), dd.getTentinh()});
        }
        }catch(SQLException ex){
            
        }
    }
    void loadmaTinh(){
        try{
            ArrayList<TblTinh> T= dddl.getListT();
            for(TblTinh t:T){
                cboTinh.addItem(t);
            }
        }catch(Exception ex){
            
        }
    }
    private boolean KiemTraDL() throws HeadlessException{
        StringBuilder sb= new StringBuilder();
        if(txtTenDD.getText().equals("")){
            sb.append("VUI LÒNG NHẬP TÊN ĐỊA ĐIỂM!\n");
            txtTenDD.setBackground(Color.yellow);
            txtTenDD.requestFocus();
        }
        else{
            txtTenDD.setBackground(Color.white);
            
        }
        if(cboTinh.getSelectedItem().equals("")){
            sb.append("VUI LÒNG CHỌN TỈNH!\n");
            cboTinh.setBackground(Color.yellow);
            cboTinh.requestFocus();
        }
        else{
            cboTinh.setBackground(Color.white);
            
        }
        if(sb.length()>0){
            JOptionPane.showConfirmDialog(this, sb.toString(),"Thông báo",JOptionPane.ERROR_MESSAGE);
            return false;
        }
        else{
            return true;
        }
        
    }
