    private void btnLuuMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnLuuMouseClicked
        // TODO add your handling code here:
        if(them){
           if(KiemTraDL()){
               try {
                   String tendiadiem= txtTenDD.getText();
                   TblTinh t= (TblTinh) cboTinh.getSelectedItem();
                            String matinh=String.valueOf(t.getMatinh());
                   

                   Connection conn= dao.getConnect();
                   
                   String caulenh="INSERT INTO tbl_diadiem VALUES (null,'"+tendiadiem+"','"+matinh+"')";
                   boolean a= dao.Lenh(caulenh, conn);
                   if(a){
                       tblDD.removeAll();
                       tablemodel.setRowCount(0);
                       loadDD();
                       JOptionPane.showMessageDialog(this, "THÊM THÀNH CÔNG!","Thông báo",JOptionPane.INFORMATION_MESSAGE);
                   }
                   else{
                       JOptionPane.showMessageDialog(this, "THÊM KHÔNG THÀNH CÔNG!","Thông báo",JOptionPane.ERROR_MESSAGE);
                   }
               } catch (SQLException ex) {
                   
               }
           } else{
            JOptionPane.showMessageDialog(this, "NHẬP SAI VUI LÒNG NHẬP LẠI!","Thông báo",JOptionPane.ERROR_MESSAGE);
        }
        }
       
           
        if(sua){
             if(KiemTraDL()){
                
            try{
                Integer MaDiadiem=Integer.parseInt(txtMaDD.getText());
                String tendiadiem= txtTenDD.getText();
                   TblTinh t= (TblTinh) cboTinh.getSelectedItem();
                            String matinh=String.valueOf(t.getMatinh());

                Connection conn= dao.getConnect();
                String caulenh="UPDATE tbl_diadiem SET tendd='"+tendiadiem+"',matinh='"+matinh+"'where madd="+MaDiadiem;
                boolean a= dao.Lenh(caulenh, conn);
                if(a){
                    tblDD.removeAll();
                    tablemodel.setRowCount(0);
                    loadDD();
                    JOptionPane.showMessageDialog(this, "SỬA THÀNH CÔNG!","Thông báo",JOptionPane.INFORMATION_MESSAGE);
                }
                else{
                    JOptionPane.showMessageDialog(this, "SỬA KHÔNG THÀNH CÔNG!","Thông báo",JOptionPane.ERROR_MESSAGE);
                }
            } catch (SQLException ex) {
                
            }
        }
        else{
            JOptionPane.showMessageDialog(this, "NHẬP SAI VUI LÒNG NHẬP LẠI!","Thông báo",JOptionPane.ERROR_MESSAGE);
            }
        }
    }//GEN-LAST:event_btnLuuMouseClicked

