    private void btnXoaMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnXoaMouseClicked
        // TODO add your handling code here:
        int chon= JOptionPane.showConfirmDialog(this,"BẠN CÓ MUỐN XÓA?","Thông báo",JOptionPane.YES_NO_OPTION, JOptionPane.QUESTION_MESSAGE);
            if(chon== JOptionPane.YES_OPTION){
                try {
                    Integer MaDiadiem= Integer.parseInt(txtMaDD.getText());
                    Connection conn= dao.getConnect();
                    String caulenh="DELETE FROM tbl_diadiem WHERE madd="+MaDiadiem;
                    boolean a= dao.Lenh(caulenh, conn);
                    if(a){
                        tblDD.removeAll();
                        tablemodel.setRowCount(0);
                        loadDD();
                        JOptionPane.showMessageDialog(this, "XÓA THÀNH CÔNG!","Thông báo",JOptionPane.INFORMATION_MESSAGE);
                    }
                    else{ 
                        JOptionPane.showMessageDialog(this, "XÓA KHÔNG THÀNH CÔNG!","Thông báo",JOptionPane.ERROR_MESSAGE);
                    }
                } catch (SQLException ex) {
                   
                }
            }
    }//GEN-LAST:event_btnXoaMouseClicked

    private void tblDDMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tblDDMouseClicked
        // TODO add your handling code here:
        int i= tblDD.getSelectedRow();
        hienThi(i);
    }//GEN-LAST:event_tblDDMouseClicked

    private void btnHuyMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnHuyMouseClicked
        // TODO add your handling code here:
        txtMaDD.setText("");
        txtTenDD.setText("");
        txtTimKiem.setText("");
        btnThem.setEnabled(true);
        btnSua.setEnabled(true);
        btnXoa.setEnabled(true);
        txtTenDD.requestFocus();
        tblDD.removeAll();
        tablemodel.setRowCount(0);
        loadDD();
    }//GEN-LAST:event_btnHuyMouseClicked

    private void btnTimKiemMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnTimKiemMouseClicked
        // TODO add your handling code here:
        tblDD.removeAll();
        tablemodel.setRowCount(0);
        
        String timkiem=txtTimKiem.getText();
        ArrayList<TblDiadiem>list;
        try{
        list = dddl.getListDD(timkiem);
        tablemodel = (DefaultTableModel) tblDD.getModel();
        for(TblDiadiem dd : list){
            tablemodel.addRow(new Object[]{dd.getMadd(), dd.getTendd(), dd.getTentinh()});
        }
        }catch(SQLException ex){
            
        }
    }//GEN-LAST:event_btnTimKiemMouseClicked

