    void hienThi(int i){
        TableModel model= tblDD.getModel();
        txtMaDD.setText(model.getValueAt(i,0).toString());
        txtTenDD.setText(model.getValueAt(i,1).toString());
        String cbosubT=model.getValueAt(i, 2).toString();
        for(int j=0;j<cboTinh.getItemCount();j++){
            if(cboTinh.getItemAt(j).toString().equalsIgnoreCase(cbosubT)){
                cboTinh.setSelectedIndex(j);
            }
        }
        
    }
    
