    void loadAD(){
        ArrayList<TblAdmin>list;
        try{
            list = addl.getListAd();
            tablemodel = (DefaultTableModel) tblAD.getModel();
            for(TblAdmin ad : list){
                tablemodel.addRow(new Object[]{ad.getTaikhoan(), ad.getMatkhau(), ad.getManv(), ad.getQuyen(), ad.getHinhanh()});
            }
        }catch(SQLException ex){
            
        }
        
    }
