    String taiKhoan;
    private void btnXoa1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnXoa1MouseClicked
        // TODO add your handling code here:
        int chon= JOptionPane.showConfirmDialog(this,"BẠN CÓ MUỐN XÓA?","Thông báo",JOptionPane.YES_NO_OPTION, JOptionPane.QUESTION_MESSAGE);
            if(chon== JOptionPane.YES_OPTION){
                try {
                    
                    Connection conn= dao.getConnect();
                    
                    String caulenh="DELETE FROM tbl_admin WHERE taikhoan="+taiKhoan;
                    boolean a= dao.Lenh(caulenh, conn);
                    
                    if(a){
                        tblAD.removeAll();
                        tablemodel.setRowCount(0);
                        loadAD();
                        JOptionPane.showMessageDialog(this, "XÓA THÀNH CÔNG!","Thông báo",JOptionPane.INFORMATION_MESSAGE);
                    }
                    else{ 
                        JOptionPane.showMessageDialog(this, "XÓA KHÔNG THÀNH CÔNG!","Thông báo",JOptionPane.ERROR_MESSAGE);
                    }
                } catch (SQLException ex) {
                   
                }
            }
    }//GEN-LAST:event_btnXoa1MouseClicked

    private void tblADMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tblADMouseClicked
        // TODO add your handling code here:
        int i= tblAD.getSelectedRow();
        TableModel model= tblAD.getModel();
        taiKhoan=model.getValueAt(i,0).toString();
    }//GEN-LAST:event_tblADMouseClicked


